mapper.select:
    select p.visit_id, p.customer_id, p.visit_name, p.visit_phone, p.visit_content, p.visit_date, p.next_time, p.visit_type, p.visit_status, p.del_flag, p.create_by, p.create_time, p.update_by, p.update_time, p.remark,c.customer_name,c.customer_type from crm_visit p
	left join crm_customer c on p.customer_id = c.customer_id
	left join sys_user u on c.create_by=u.login_name
	left join sys_dept d on u.dept_id=d.dept_id
      
    	p.del_flag='0'
         and p.customer_id = #{customerId}
         and p.visit_name like concat('%', #{visitName}, '%')
         and p.visit_phone like concat('%', #{visitPhone}, '%')
         and p.visit_date = #{visitDate}
         and p.visit_status = #{visitStatus}

mapper.update:
update crm_visit
customer_id = #{customerId}, visit_name = #{visitName}, visit_phone = #{visitPhone}, visit_content = #{visitContent}, visit_date = #{visitDate}, next_time = #{nextTime}, visit_type = #{visitType}, visit_status = #{visitStatus}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where visit_id = #{visitId}
