mapper.select:
select p.follow_id, p.clue_id, p.follow_date, p.follow_content, p.follow_type, p.del_flag, p.create_by,
p.create_time, p.update_by, p.update_time, p.remark,c.name from crm_clue_follow p
left join crm2_clue c on p.clue_id = c.clue_id
left join sys_user u on u.login_name=c.belong_to
left join sys_dept d on u.dept_id=d.dept_id

    p.del_flag='0'
    and p.clue_id = #{clueId}
    and p.follow_date = #{followDate}
    and p.follow_content = #{followContent}
    and p.follow_type = #{followType}
    and c.customer_name like concat('%',
        #{params.customerName}, '%')


        AND date_format(p.follow_date,'%y%m%d') >= date_format(#{params.beginFollowDate},'%y%m%d')


        AND date_format(p.follow_date,'%y%m%d') <= date_format(#{params.endFollowDate},'%y%m%d')


    ${params.dataScope}

select p.follow_id, p.clue_id, p.follow_date, p.follow_content, p.follow_type, p.del_flag, p.create_by,
p.create_time, p.update_by, p.update_time, p.remark,c.name from crm_clue_follow p
left join crm_clue c on p.clue_id = c.clue_id
left join sys_user u on c.create_by=u.login_name
left join sys_dept d on u.dept_id=d.dept_id

    p.del_flag='0'
    and p.clue_id = #{clueId}
    and p.follow_date = #{followDate}
    and p.follow_content = #{followContent}
    and p.follow_type = #{followType}
    and p.follow_status = #{followStatus}

mapper.update:
update crm_clue_follow
clue_id = #{clueId}, follow_date = #{followDate}, follow_content = #{followContent}, follow_type = #{followType}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where follow_id = #{followId}

mapper.delete:
update crm_clue_follow
set del_flag="1"
where follow_id = #{followId}
