mapper.select:
        select p.complaint_id, p.customer_id, p.complaint_date, p.complaint_name, p.complaint_phone, p.complaint_content, p.complaint_type, p.complaint_status, p.del_flag, p.create_by, p.create_time, p.update_by, p.update_time, p.remark,c.customer_name,c.customer_type  from crm_complaint p
    	left join crm_customer c on p.customer_id = c.customer_id
    	left join sys_user u on u.login_name=c.belong_to
    	left join sys_dept d on u.dept_id=d.dept_id
          
        	p.del_flag='0'
             and p.customer_id = #{customerId}
             and p.complaint_date = #{complaintDate}
             and p.complaint_name like concat('%', #{complaintName}, '%')
             and p.complaint_type = #{complaintType}
             and p.complaint_status = #{complaintStatus}
             and c.customer_name like concat('%', #{params.customerName}, '%')
            
				AND date_format(p.complaint_date,'%y%m%d') >= date_format(#{params.beginComplaintDate},'%y%m%d')
			
			
				AND date_format(p.complaint_date,'%y%m%d') <= date_format(#{params.endComplaintDate},'%y%m%d')
			
			
			${params.dataScope}

    select p.complaint_id, p.customer_id, p.complaint_date, p.complaint_name, p.complaint_phone, p.complaint_content, p.complaint_type, p.complaint_status, p.del_flag, p.create_by, p.create_time, p.update_by, p.update_time, p.remark,c.customer_name,c.customer_type  from crm_complaint p
	left join crm_customer c on p.customer_id = c.customer_id
	left join sys_user u on c.create_by=u.login_name
	left join sys_dept d on u.dept_id=d.dept_id
      
    	p.del_flag='0'
         and p.customer_id = #{customerId}
         and p.complaint_date = #{complaintDate}
         and p.complaint_name like concat('%', #{complaintName}, '%')
         and p.complaint_type = #{complaintType}
         and p.complaint_status = #{complaintStatus}

mapper.update:
update crm_complaint
customer_id = #{customerId}, complaint_date = #{complaintDate}, complaint_name = #{complaintName}, complaint_phone = #{complaintPhone}, complaint_content = #{complaintContent}, complaint_type = #{complaintType}, complaint_status = #{complaintStatus}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where complaint_id = #{complaintId}
